'use strict';

const v3 = require('../../../index').v3
  , Scene = v3.Scene
  , GroupLightState = v3.lightStates.GroupLightState
;
// If using this code outside of this library the above should be replaced with
// const v3 = require('node-hue-api').v3
//  , Scene = v3.Scene
// ;

// Replace this with your username for accessing the bridge
const USERNAME = require('../../../test/support/testValues').username;

// Set this to an existing scene id
const SCENE_ID = 'BW0qV8ys7otEdex';

v3.discovery.nupnpSearch()
  .then(searchResults => {
    const host = searchResults[0].ipaddress;
    return v3.api.createLocal(host).connect(USERNAME);
  })
  .then(api => {
    // return api.scenes.getAll();
    // return api.sensors.getAll();
    return api.sensors.get(189)
      .then(sensor => {
        sensor.status = 0;
        return api.sensors.updateSensorState(sensor);
      });
  })
  .then(scenes => {
    console.log(JSON.stringify(scenes));
    // scenes.forEach(scene => {
    //   console.log(scene.toStringDetailed());
    // });
  })
  .catch(err => {
    if (err.getHueErrorType() === 3) {
      console.error(`Failed to resolve an existing scene with id:${SCENE_ID}`);
    } else {
      console.error(`Unexpected Error: ${err.message}`);
    }
  })
;
